package templates

const fileTpl = `// Code generated by protoc-gen-zap
// source: {{ .InputPath }}
// DO NOT EDIT!!!
package {{ .Package.GoName }}
import (
	"go.uber.org/zap/zapcore"
	"github.com/bigbluedisco/protoc-gen-zap/utils"
)

// ensure the imports are used
var (
	_ = (*utils.Interfaces)(nil)
	_ = zapcore.InfoLevel
)

{{ range .AllMessages }}
	{{ template "message" . }}
{{ end }}
`
